mapper.select:
SELECT *, ROW_NUMBER() OVER() as no, 0 as open FROM (SELECT SERVICE_CD as serviceCd, CASE WHEN SERVICE_CD = '00' THEN 'Zabbix' WHEN SERVICE_CD = '01' THEN 'Postman' WHEN SERVICE_CD = '02' THEN 'Sefilcare' WHEN SERVICE_CD = '03' THEN 'CheckServer' END as serviceNm, LOG_SEQ as logSeq, LOG_PRIORITY as status, LOG_TYPE as logType, DATE_FORMAT(CONCAT(LOG_DT, LOG_TM), '%Y-%m-%d %H:%i:%s') as date, TITLE as title, CONTENT as content FROM TB_LOG_HISTORY JOIN TB_LISTENING_SERVERS USING (SERVICE_CD) WHERE USE_CL = '1'
AND SERVICE_CD=#{serviceCd}
AND LOG_PRIORITY=#{status}
AND LOG_DT BETWEEN #{startDt}
AND #{endDt}
ORDER BY date desc, serviceCd asc ) RSLT WHERE serviceCd IN (#{ZABBIX}, #{POSTMAN}, #{SEFILCARE}, #{CHECK_SERVER})

mapper.update:
UPDATE TB_USER
SET AUTH = ( SELECT CODE_CL FROM TB_CODE WHERE DESC1 = #{authStr} )
WHERE UID = #{uid}


UPDATE TB_USER
SET EMAIL = ''
WHERE UID = #{uid}

UPDATE TB_USER
SET EMAIL = #{email}
WHERE UID = #{uid}

UPDATE TB_USER
SET TEL_NUM = ''
WHERE UID = #{uid}

UPDATE TB_USER
SET TEL_NUM = #{tel}
WHERE UID = #{uid}

UPDATE TB_KEYWORD
SET USE_CL = #{useCl}
WHERE KEYWORD = #{keyword} AND SERVICE_CD = #{serviceCd}
